INSERT INTO `wp_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`, `layout`, `image`, `is_deleted`, `icon`) VALUES
(1, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Sztuczki, aktualizacje produktu i inspiracje', 'Jesteśmy dla ciebie - otrzymaj wskazówki, aktualności i inspiracje prosto do swojej skrzynki mailowej.', '{}', 'unactioned', 'woocommerce-admin', '2020-12-31 16:13:33', NULL, 0, 'plain', '', 0, 'info'),
(2, 'wc-admin-marketing-intro', 'info', 'en_US', 'Skontaktuj się z klientami', 'Rozwiń bazę klientów i zwiększ sprzedaż dzięki narzędziom marketingowym wbudowanym w WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2020-12-31 16:13:33', NULL, 0, 'plain', '', 0, 'info'),
(3, 'ecomm-need-help-setting-up-your-store', 'info', 'en_US', 'Need help setting up your Store?', 'Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. We’re happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.', '{}', 'pending', 'woocommerce.com', '2020-12-29 17:13:34', NULL, 0, 'plain', '', 0, 'info'),
(4, 'woocommerce-services', 'info', 'en_US', 'WooCommerce Shipping & Tax', 'WooCommerce Shipping &amp; Tax helps get your store \"ready to sell\" as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.', '{}', 'pending', 'woocommerce.com', '2020-12-29 17:13:34', NULL, 0, 'plain', '', 0, 'info'),
(5, 'ecomm-unique-shopping-experience', 'info', 'en_US', 'For a shopping experience as unique as your customers', 'Product Add-Ons allow your customers to personalize products while they\'re shopping on your online store. No more follow-up email requests—customers get what they want, before they\'re done checking out. Learn more about this extension that comes included in your plan.', '{}', 'pending', 'woocommerce.com', '2020-12-29 17:13:34', NULL, 0, 'plain', '', 0, 'info'),
(6, 'wcpay-promo-2020-11', 'marketing', 'en_US', 'wcpay-promo-2020-11', 'wcpay-promo-2020-11', '{}', 'pending', 'woocommerce.com', '2020-12-29 20:13:34', NULL, 0, 'plain', '', 0, 'info'),
(49, 'wcpay-promo-2021-6-incentive-2', 'marketing', 'en_US', 'Simplify the payments process for you and your customers with WooCommerce Payments', 'With <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay601\">WooCommerce Payments</a>, you can securely accept all major cards, Apple Pay®, and recurring revenue in over 100 currencies.\n				Built into your store’s WooCommerce dashboard, track cash flow and manage all of your transactions in one place – with no setup costs or monthly fees.\n				<br /><br />\n				By clicking \"Get WooCommerce Payments,\" you agree to the <a href=\"https://wordpress.com/tos/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay601\">Terms of Service</a>\n				and acknowledge you have read the <a href=\"https://automattic.com/privacy/\">Privacy Policy</a>.\n				', '{}', 'pending', 'woocommerce.com', '2022-01-13 19:29:13', NULL, 0, 'plain', '', 0, 'info'),
(7, 'wcpay-subscriptions-2020-11', 'marketing', 'en_US', 'Manage subscriber payments from your store\'s dashboard', 'Securely accept cards and manage transactions right from your dashboard with <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Payments</a>  – now supporting <a href=\"https://woocommerce.com/products/woocommerce-subscriptions/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Subscriptions</a>! <br /><br /><em>By clicking \"Install now,\" you agree to our <a href=\"https://wordpress.com/tos/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">Terms of Service</a>.</em>', '{}', 'pending', 'woocommerce.com', '2020-12-30 07:13:34', NULL, 0, 'plain', '', 0, 'info'),
(8, 'wcpay-promo-2020-12', 'marketing', 'en_US', 'wcpay-promo-2020-12', 'wcpay-promo-2020-12', '{}', 'pending', 'woocommerce.com', '2020-12-29 20:13:34', NULL, 0, 'plain', '', 0, 'info'),
(48, 'wcpay-promo-2021-6-incentive-1', 'marketing', 'en_US', 'Simplify the payments process for you and your customers with WooCommerce Payments', 'With <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay601\">WooCommerce Payments</a>, you can securely accept all major cards, Apple Pay®, and recurring revenue in over 100 currencies.\n				Built into your store’s WooCommerce dashboard, track cash flow and manage all of your transactions in one place – with no setup costs or monthly fees.\n				<br /><br />\n				By clicking \"Get WooCommerce Payments,\" you agree to the <a href=\"https://wordpress.com/tos/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay601\">Terms of Service</a>\n				and acknowledge you have read the <a href=\"https://automattic.com/privacy/\">Privacy Policy</a>.\n				', '{}', 'pending', 'woocommerce.com', '2022-01-13 19:29:13', NULL, 0, 'plain', '', 0, 'info'),
(9, 'wcpay-subscriptions-2020-12', 'marketing', 'en_US', 'Manage payments from your store\'s dashboard', 'Securely accept cards and manage transactions right from your dashboard with <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Payments</a>! Zero setup fees or monthly fees. Just pay-as-you-go, starting at just 2.9% + $0.30 per transaction for U.S.-issued cards. <br /><br /><em>By clicking \"Install now,\" you agree to our <a href=\"https://wordpress.com/tos/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">Terms of Service</a>.</em>', '{}', 'pending', 'woocommerce.com', '2020-12-30 07:13:34', NULL, 0, 'plain', '', 0, 'info'),
(10, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Połącz z WooCommerce.com', 'Podłącz, aby uzyskać ważne powiadomienia o produktach i aktualizacjach.', '{}', 'unactioned', 'woocommerce-admin', '2020-12-31 16:13:34', NULL, 0, 'plain', '', 0, 'info'),
(11, 'wc-admin-coupon-page-moved', 'update', 'en_US', 'Zarządzanie kuponami zostało przeniesione!', 'Coupons can now be managed from Marketing &gt; Coupons. Click the button below to remove the legacy WooCommerce &gt; Coupons menu item.', '{}', 'unactioned', 'woocommerce-admin', '2020-12-31 16:39:38', NULL, 0, 'plain', '', 0, 'info'),
(12, 'wc-admin-mobile-app', 'info', 'en_US', 'Zainstaluj aplikację mobilną Woo', 'Zainstaluj aplikację mobilną WooCommerce, by zarządzać zamówieniami, otrzymywać powiadomienia o sprzedaży i podglądać kluczowe dane gdziekolwiek jesteś.', '{}', 'unactioned', 'woocommerce-admin', '2021-01-02 16:13:36', NULL, 0, 'plain', '', 0, 'info'),
(13, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Pomóż usprawniać WooCommerce poprzez śledzenie danych użytkowania', 'Zbieranie danych użytkowania pozwala nam rozwijać WooCommerce. Twój sklep będzie brany pod uwagę podczas oceny nowych funkcjonalności, oceny jakości aktualizacji, i decyzji czy poprawki są sensowne. W każdym momencie możesz przejść do <a href=\"http://localhost/wordpress/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Ustawień</a> i wyłączyć dzielenie się danymi. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">Dowiedz się więcej</a> na temat danych, które zbieramy.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-02 18:23:28', NULL, 0, 'plain', '', 0, 'info'),
(14, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Rozpocznij przyjmowanie płatności w swoim sklepie!', 'Przyjmuj płatności z dostawcą odpowiednim dla ciebie - wybierz z ponad 100 bramek płatniczych dla WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-02 18:23:28', NULL, 0, 'plain', '', 0, 'info'),
(15, 'wc-admin-store-notice-giving-feedback-2', 'info', 'en_US', 'Prześlij opinię', 'Now that you’ve chosen us as a partner, our goal is to make sure we\'re providing the right tools to meet your needs. We\'re looking forward to having your feedback on the store setup experience so we can improve it in the future.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-02 18:23:28', NULL, 0, 'plain', '', 0, 'info'),
(16, 'wc-admin-insight-first-sale', 'survey', 'en_US', 'Czy wiesz że?', 'Sklepy oparte o WooCommerce potrzebują średnio 31 dni aby zdobyć pierwszą sprzedaż. Jesteś na świetnej drodze do tego. Czy uważasz tego typu porady za użyteczne?', '{}', 'unactioned', 'woocommerce-admin', '2021-05-02 18:23:28', NULL, 0, 'plain', '', 0, 'info'),
(17, 'wc-admin-home-screen-feedback', 'info', 'en_US', 'Pomóż nam ulepszyć ekran główny WooCommerce', 'We\'d love your input to shape the future of the WooCommerce Home screen together. Feel free to share any feedback, ideas or suggestions that you have.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-02 18:23:28', NULL, 0, 'plain', '', 0, 'info'),
(18, 'wc-admin-real-time-order-alerts', 'info', 'en_US', 'Get real-time order alerts anywhere', 'Get notifications about store activity, including new orders and product reviews directly on your mobile devices with the Woo app.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-02 18:23:28', NULL, 0, 'plain', '', 0, 'info'),
(19, 'wc-admin-customize-store-with-blocks', 'info', 'en_US', 'Spersonalizuj swój sklep internetowy za pomocą bloków WooCommerce', 'Dzięki naszym blokom można wybierać i wyświetlać produkty, kategorie, filtry i wiele innych praktycznie w każdym miejscu twojej witryny - bez potrzeby używania shortcodów lub edycji kodu. Dowiedz się więcej o tym, jak używać każdego z nich.', '{}', 'unactioned', 'woocommerce-admin', '2021-05-02 18:23:28', NULL, 0, 'plain', '', 0, 'info'),
(20, 'paypal_ppcp_gtm_2021', 'marketing', 'en_US', 'Offer more options with the new PayPal', 'Get the latest PayPal extension for a full suite of payment methods with extensive currency and country coverage.', '{}', 'pending', 'woocommerce.com', '2021-05-02 14:23:28', NULL, 0, 'plain', '', 0, 'info'),
(21, 'facebook_pixel_api_2021', 'marketing', 'en_US', 'Improve the performance of your Facebook ads', 'Enable Facebook Pixel and Conversions API through the latest version of Facebook for WooCommerce for improved measurement and ad targeting capabilities.', '{}', 'pending', 'woocommerce.com', '2021-05-02 14:23:29', NULL, 0, 'plain', '', 0, 'info'),
(22, 'facebook_ec_2021', 'marketing', 'en_US', 'Sync your product catalog with Facebook to help boost sales', 'A single click adds all products to your Facebook Business Page shop. Product changes are automatically synced, with the flexibility to control which products are listed.', '{}', 'pending', 'woocommerce.com', '2021-05-02 14:23:29', NULL, 0, 'plain', '', 0, 'info'),
(23, 'wc-admin-getting-started-in-ecommerce', 'info', 'en_US', 'Getting Started in eCommerce - webinar', 'We want to make eCommerce and this process of getting started as easy as possible for you. Watch this webinar to get tips on how to have our store up and running in a breeze.', '{}', 'unactioned', 'woocommerce.com', '2021-05-01 16:23:29', NULL, 0, 'plain', '', 0, 'info'),
(24, 'your-first-product', 'info', 'en_US', 'Your first product', 'That’s huge! You’re well on your way to building a successful online store — now it’s time to think about how you’ll fulfill your orders.<br /><br />Read our shipping guide to learn best practices and options for putting together your shipping strategy. And for WooCommerce stores in the United States, you can print discounted shipping labels via USPS with <a href=\"https://href.li/?https://woocommerce.com/shipping\" target=\"_blank\">WooCommerce Shipping</a>.', '{}', 'pending', 'woocommerce.com', '2021-05-01 16:23:29', NULL, 0, 'plain', '', 0, 'info'),
(25, 'wc-square-apple-pay-boost-sales', 'marketing', 'en_US', 'Boost sales with Apple Pay', 'Now that you accept Apple Pay® with Square you can increase conversion rates by letting your customers know that Apple Pay® is available. Here’s a marketing guide to help you get started.', '{}', 'pending', 'woocommerce.com', '2021-05-02 14:23:29', NULL, 0, 'plain', '', 0, 'info'),
(26, 'wc-square-apple-pay-grow-your-business', 'marketing', 'en_US', 'Grow your business with Square and Apple Pay ', 'Now more than ever, shoppers want a fast, simple, and secure online checkout experience. Increase conversion rates by letting your customers know that you now accept Apple Pay®.', '{}', 'pending', 'woocommerce.com', '2021-05-02 14:23:29', NULL, 0, 'plain', '', 0, 'info'),
(27, 'wcpay-apple-pay-is-now-available', 'marketing', 'en_US', 'Apple Pay is now available with WooCommerce Payments!', 'Increase your conversion rate by offering a fast and secure checkout with <a href=\"https://woocommerce.com/apple-pay/?utm_source=inbox&amp;utm_medium=product&amp;utm_campaign=wcpay_applepay\" target=\"_blank\">Apple Pay</a>®. It’s free to get started with <a href=\"https://woocommerce.com/payments/?utm_source=inbox&amp;utm_medium=product&amp;utm_campaign=wcpay_applepay\" target=\"_blank\">WooCommerce Payments</a>.', '{}', 'pending', 'woocommerce.com', '2021-05-02 14:23:29', NULL, 0, 'plain', '', 0, 'info'),
(28, 'wcpay-apple-pay-boost-sales', 'marketing', 'en_US', 'Boost sales with Apple Pay', 'Now that you accept Apple Pay® with WooCommerce Payments you can increase conversion rates by letting your customers know that Apple Pay® is available. Here’s a marketing guide to help you get started.', '{}', 'pending', 'woocommerce.com', '2021-05-02 14:23:29', NULL, 0, 'plain', '', 0, 'info'),
(29, 'wcpay-apple-pay-grow-your-business', 'marketing', 'en_US', 'Grow your business with WooCommerce Payments and Apple Pay', 'Now more than ever, shoppers want a fast, simple, and secure online checkout experience. Increase conversion rates by letting your customers know that you now accept Apple Pay®.', '{}', 'pending', 'woocommerce.com', '2021-05-02 14:23:29', NULL, 0, 'plain', '', 0, 'info'),
(30, 'wc-admin-optimizing-the-checkout-flow', 'info', 'en_US', 'Optimizing the checkout flow', 'It’s crucial to get your store’s checkout as smooth as possible to avoid losing sales. Let’s take a look at how you can optimize the checkout experience for your shoppers.', '{}', 'pending', 'woocommerce.com', '2021-05-01 16:23:29', NULL, 0, 'plain', '', 0, 'info'),
(31, 'wc-admin-first-five-things-to-customize', 'info', 'en_US', 'The first 5 things to customize in your store', 'Deciding what to start with first is tricky. To help you properly prioritize, we’ve put together this short list of the first few things you should customize in WooCommerce.', '{}', 'unactioned', 'woocommerce.com', '2021-05-01 16:23:29', NULL, 0, 'plain', '', 0, 'info'),
(32, 'wc-payments-qualitative-feedback', 'info', 'en_US', 'WooCommerce Payments setup - let us know what you think', 'Congrats on enabling WooCommerce Payments for your store. Please share your feedback in this 2 minute survey to help us improve the setup process.', '{}', 'pending', 'woocommerce.com', '2021-05-01 16:23:29', NULL, 0, 'plain', '', 0, 'info'),
(33, 'share-your-feedback-on-paypal', 'info', 'en_US', 'Share your feedback on PayPal', 'Share your feedback in this 2 minute survey about how we can make the process of accepting payments more useful for your store.', '{}', 'pending', 'woocommerce.com', '2021-05-23 17:18:52', NULL, 0, 'plain', '', 0, 'info'),
(34, 'wcpay_instant_deposits_gtm_2021', 'marketing', 'en_US', 'Get paid within minutes – Instant Deposits for WooCommerce Payments', 'Stay flexible with immediate access to your funds when you need them – including nights, weekends, and holidays. With <a href=\"https://woocommerce.com/products/woocommerce-payments/?utm_source=inbox&amp;utm_medium=product&amp;utm_campaign=wcpay_instant_deposits\">WooCommerce Payments\'</a> new Instant Deposits feature, you’re able to transfer your earnings to a debit card within minutes.', '{}', 'pending', 'woocommerce.com', '2021-05-24 15:18:52', NULL, 0, 'plain', '', 0, 'info'),
(35, 'wc-admin-edit-products-on-the-move', 'info', 'en_US', 'Edit products on the move', 'Edit and create new products from your mobile devices with the Woo app', '{}', 'unactioned', 'woocommerce-admin', '2022-01-13 20:29:12', NULL, 0, 'plain', '', 0, 'info'),
